const item = items[0];
let cleaned_text = "";

if (item?.json?.pages) {
    cleaned_text = item.json.pages.map(page => {
        let markdown = page.markdown || "";

        // ✅ Normalize Windows line endings (\r\n) to \n
        markdown = markdown.replace(/\r\n/g, '\n');

        // 1️⃣ Remove image markdown patterns like ![img](url)
        let cleaned_page = markdown.replace(/!\[.*?\]\(.*?\)/g, "").trim();

        // 2️⃣ Remove Markdown headers (#, ##, etc.)
        cleaned_page = cleaned_page.replace(/^#+\s*/gm, "").trim();

        // 3️⃣ Replace single newlines with a space (preserve paragraph breaks)
        cleaned_page = cleaned_page.replace(/(?<!\n)\n(?!\n)/g, ' ');

        // 4️⃣ Collapse multiple spaces into one
        cleaned_page = cleaned_page.replace(/\s{2,}/g, ' ');

        // 5️⃣ Consolidate multiple paragraph breaks into exactly two newlines
        cleaned_page = cleaned_page.replace(/\n\n+/g, '\n\n').trim();

        return cleaned_page;
    }).join("\n\n"); // ✅ Join pages with paragraph breaks, not spaces
}

// ✅ Output cleaned text to next node
return [
  { json: { cleaned_text } }
];
